using UnityEngine;public static class LevelEditorInputs {	public static bool GetCameraUp() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.UpArrow) || Input.GetKey(KeyCode.W))				return true;				/*	Mobile inputs.		 *	If the player swipes downwards, the camera moves upwards.		*/		#else			if(GlobalInputs.GetSwipe(Vector2.down, true))				return true;		#endif		return false;	}	public static bool GetCameraLeft() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.LeftArrow) || Input.GetKey(KeyCode.A))				return true;		/*	Mobile inputs.		 *	If the player swipes rightwards, the camera moves leftwards.		*/		#else			if(GlobalInputs.GetSwipe(Vector2.right, true))				return true;		#endif		return false;	}	public static bool GetCameraDown() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.DownArrow) || Input.GetKey(KeyCode.S))				return true;		/*	Mobile inputs.		 *	If the player swipes upwards, the camera moves downwards.		*/		#else			if(GlobalInputs.GetSwipe(Vector2.up, true))				return true;		#endif		return false;	}	public static bool GetCameraRight() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.RightArrow) || Input.GetKey(KeyCode.D))				return true;					/*	Mobile inputs.		 *	If the player swipes leftwards, the camera moves rightwards.		*/		#else			if(GlobalInputs.GetSwipe(Vector2.left, true))				return true;		#endif		return false;	}	public static float GetZoomIn() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.PageUp) || Input.GetKey(KeyCode.Equals) || Input.GetKey(KeyCode.Plus) || Input.GetKey(KeyCode.KeypadPlus))
				return 1f;
			else if(Input.GetAxisRaw("Mouse ScrollWheel") > 0)				return 4f;		#else			if(GlobalInputs.GetPinchOut())				return 1f;		#endif		return 0;	}	public static float GetZoomOut() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.PageDown) || Input.GetKey(KeyCode.Minus) || Input.GetKey(KeyCode.KeypadMinus))
				return 1f;
			else if(Input.GetAxisRaw("Mouse ScrollWheel") < 0)				return 4f;		#else			if(GlobalInputs.GetPinchIn())				return 1f;		#endif		return 0;	}	public static bool GetBrush() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetMouseButton(0))				return true;        /*	Mobile inputs.		 *	Returns true if the taps the screen.		*/        #else        	if(Input.GetButton("Fire1"))				return true;			/*if(GlobalInputs.GetSwipe(Vector2.zero, false))				return true;*/        #endif        return false;	}	public static bool Rotate() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetMouseButtonDown(0))				return true;        /*	Mobile inputs.		 *	Returns true if the taps the screen.		*/        #else			if(GlobalInputs.GetRotate())				return true;        #endif        return false;	}}