using UnityEngine;public static class LevelEditorInputs {	public static bool GetCameraUp(bool isDraging = false) {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.UpArrow) || Input.GetKey(KeyCode.W))				return true;			else if(isDraging && Input.mousePosition.y > Camera.main.pixelHeight * 0.98f)				return true;		#else			if(GlobalInputs.GetSwipe(Vector2.down, true))				return true;			else if(Input.touchCount == 1 && isDraging && Input.GetTouch(0).position.y > Camera.main.pixelHeight * 0.98f)				return true;		#endif		return false;	}	public static bool GetCameraLeft(bool isDraging = false) {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.LeftArrow) || Input.GetKey(KeyCode.A))				return true;			else if(isDraging && Input.mousePosition.x < Camera.main.pixelWidth * 0.02f)				return true;		#else			if(GlobalInputs.GetSwipe(Vector2.right, true))				return true;			else if(Input.touchCount == 1 && isDraging && Input.GetTouch(0).position.x < Camera.main.pixelWidth * 0.02f)				return true;		#endif		return false;	}	public static bool GetCameraDown(bool isDraging = false) {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.DownArrow) || Input.GetKey(KeyCode.S))				return true;			else if(isDraging && Input.mousePosition.y < Camera.main.pixelHeight * 0.02f)				return true;		#else			if(GlobalInputs.GetSwipe(Vector2.up, true))				return true;			else if(Input.touchCount == 1 && isDraging && Input.GetTouch(0).position.y < Camera.main.pixelHeight * 0.02f)				return true;		#endif		return false;	}	public static bool GetCameraRight(bool isDraging = false) {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.RightArrow) || Input.GetKey(KeyCode.D))				return true;			else if(isDraging && Input.mousePosition.x > Camera.main.pixelWidth * 0.98f)				return true;		#else			if(GlobalInputs.GetSwipe(Vector2.left, true))				return true;			else if(Input.touchCount == 1 && isDraging && Input.GetTouch(0).position.x > Camera.main.pixelWidth * 0.98f)				return true;		#endif		return false;	}	public static float GetZoomIn() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.PageUp) || Input.GetKey(KeyCode.Equals) || Input.GetKey(KeyCode.Plus) || Input.GetKey(KeyCode.KeypadPlus))
				return 1f;
			else if(Input.GetAxisRaw("Mouse ScrollWheel") > 0)				return 8f;		#else			if(GlobalInputs.GetPinchOut())				return 1f;		#endif		return 0;	}	public static float GetZoomOut() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetKey(KeyCode.PageDown) || Input.GetKey(KeyCode.Minus) || Input.GetKey(KeyCode.KeypadMinus))
				return 1f;
			else if(Input.GetAxisRaw("Mouse ScrollWheel") < 0)				return 8f;		#else			if(GlobalInputs.GetPinchIn())				return 1f;		#endif		return 0;	}	public static bool GetBrush() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetMouseButton(0))				return true;        /*	Mobile inputs.		 *	Returns true if the taps the screen.		*/        #else        	if(Input.GetButton("Fire1"))				return true;			/*if(GlobalInputs.GetSwipe(Vector2.zero, false))				return true;*/        #endif        return false;	}	public static bool GetEraser() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetMouseButton(1))				return true;
		#endif

		return false;
	}	public static bool Rotate() {		#if UNITY_STANDALONE || UNITY_WEBPLAYER || UNITY_EDITOR			if(Input.GetMouseButtonDown(0))				return true;        /*	Mobile inputs.		 *	Returns true if the taps the screen.		*/        #else			if(GlobalInputs.GetRotate())				return true;        #endif        return false;	}}