using UnityEngine;using System.Collections;using UnityEngine.SceneManagement;public class SplashScreenUI : MonoBehaviour {	    void Start () {		StartCoroutine(waitLoadingLevel());    }			//Wait 3 seconds on the splash screen before loading the main menu.	IEnumerator waitLoadingLevel() {		yield return new WaitForSeconds(3);		SceneManager.LoadScene("LevelSelection");	}}